import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { FiX, FiMessageSquare, FiSun } from 'react-icons/fi';
import { chatService } from '../services/chatService';

const SidebarContainer = styled.aside`
  width: 280px;
  height: 100vh;
  background: ${props => props.theme.colors.surfaceGradient};
  border-right: 2px solid ${props => props.theme.colors.border};
  display: flex;
  flex-direction: column;
  position: relative;
  transition: transform ${props => props.theme.transitions.normal};
  box-shadow: ${props => props.theme.colors.cardShadow};

  @media (max-width: ${props => props.theme.breakpoints.lg}) {
    position: fixed;
    top: 0;
    left: 0;
    z-index: 50;
    transform: translateX(${props => props.isOpen ? '0' : '-100%'});
    box-shadow: ${props => props.isOpen ? props.theme.colors.hoverShadow : 'none'};
  }
`;

const SidebarHeader = styled.div`
  padding: ${props => props.theme.spacing.lg};
  border-bottom: 2px solid ${props => props.theme.colors.border};
  background: ${props => props.theme.colors.primaryGradient};
  display: flex;
  align-items: center;
  justify-content: space-between;
  position: relative;
  
  &::after {
    content: '';
    position: absolute;
    bottom: -2px;
    left: 0;
    right: 0;
    height: 2px;
    background: ${props => props.theme.colors.accentGradient};
  }
`;

const Logo = styled.div`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
  font-size: ${props => props.theme.fontSizes.lg};
  font-weight: ${props => props.theme.fontWeights.semibold};
  color: ${props => props.theme.colors.primary};
`;

const CloseButton = styled.button`
  display: none;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  border: none;
  background: transparent;
  border-radius: ${props => props.theme.borderRadius.md};
  cursor: pointer;
  color: ${props => props.theme.colors.textSecondary};

  &:hover {
    background: ${props => props.theme.colors.surfaceAlt};
  }

  @media (max-width: ${props => props.theme.breakpoints.lg}) {
    display: flex;
  }
`;

const SidebarContent = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: ${props => props.theme.spacing.md};
`;

const Section = styled.div`
  margin-bottom: ${props => props.theme.spacing.xl};
`;

const SectionTitle = styled.h3`
  font-size: ${props => props.theme.fontSizes.sm};
  font-weight: ${props => props.theme.fontWeights.semibold};
  color: ${props => props.theme.colors.textSecondary};
  text-transform: uppercase;
  letter-spacing: 0.5px;
  margin-bottom: ${props => props.theme.spacing.md};
`;

const NavItem = styled.button`
  width: 100%;
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
  padding: ${props => props.theme.spacing.sm} ${props => props.theme.spacing.md};
  border: none;
  background: ${props => props.active ? props.theme.colors.primaryLight : 'transparent'};
  color: ${props => props.active ? props.theme.colors.primaryDark : props.theme.colors.text};
  border-radius: ${props => props.theme.borderRadius.md};
  cursor: pointer;
  font-size: ${props => props.theme.fontSizes.sm};
  transition: all ${props => props.theme.transitions.fast};
  margin-bottom: ${props => props.theme.spacing.xs};

  &:hover {
    background: ${props => props.active ? props.theme.colors.primaryLight : props.theme.colors.surfaceAlt};
  }
`;

const TipsContainer = styled.div`
  background: ${props => props.theme.colors.surfaceAlt};
  border-radius: ${props => props.theme.borderRadius.lg};
  padding: ${props => props.theme.spacing.md};
`;

const TipItem = styled.div`
  font-size: ${props => props.theme.fontSizes.sm};
  color: ${props => props.theme.colors.textSecondary};
  margin-bottom: ${props => props.theme.spacing.sm};
  line-height: 1.4;

  &:last-child {
    margin-bottom: 0;
  }

  &::before {
    content: 'ðŸ’¡';
    margin-right: ${props => props.theme.spacing.xs};
  }
`;

const LoadingTip = styled.div`
  font-size: ${props => props.theme.fontSizes.sm};
  color: ${props => props.theme.colors.textSecondary};
  font-style: italic;
`;

function Sidebar({ isOpen, onClose }) {
  const [activeNav, setActiveNav] = useState('chat');
  const [tips, setTips] = useState([]);
  const [loadingTips, setLoadingTips] = useState(true);

  useEffect(() => {
    const fetchTips = async () => {
      try {
        const data = await chatService.getFarmingTips();
        setTips(data.tips || []);
      } catch (error) {
        console.error('Failed to fetch tips:', error);
        setTips(['Check soil moisture regularly', 'Rotate your crops seasonally']);
      } finally {
        setLoadingTips(false);
      }
    };

    fetchTips();
  }, []);

  const navItems = [
    { id: 'chat', label: 'Chat Assistant', icon: FiMessageSquare },
    { id: 'weather', label: 'Weather', icon: FiSun },
  ];

  return (
    <SidebarContainer isOpen={isOpen}>
      <SidebarHeader>
        <Logo>
          ðŸŒ± KisaanSahayak
        </Logo>
        <CloseButton onClick={onClose}>
          <FiX size={16} />
        </CloseButton>
      </SidebarHeader>

      <SidebarContent>
        <Section>
          <SectionTitle>Navigation</SectionTitle>
          {navItems.map(item => (
            <NavItem
              key={item.id}
              active={activeNav === item.id}
              onClick={() => setActiveNav(item.id)}
            >
              <item.icon size={16} />
              {item.label}
            </NavItem>
          ))}
        </Section>

        <Section>
          <SectionTitle>Quick Tips</SectionTitle>
          <TipsContainer>
            {loadingTips ? (
              <LoadingTip>Loading farming tips...</LoadingTip>
            ) : (
              tips.slice(0, 3).map((tip, index) => (
                <TipItem key={index}>{tip}</TipItem>
              ))
            )}
          </TipsContainer>
        </Section>
      </SidebarContent>
    </SidebarContainer>
  );
}

export default Sidebar;
