import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { FiUser, FiUserPlus, FiUsers, FiEdit3, FiCheck, FiX } from 'react-icons/fi';
import { userService } from '../services/userService';

const UserContainer = styled.div`
  padding: ${props => props.theme.spacing.md};
  background: ${props => props.theme.colors.surfaceGradient};
  border-radius: ${props => props.theme.borderRadius.lg};
  margin-bottom: ${props => props.theme.spacing.md};
  box-shadow: ${props => props.theme.colors.cardShadow};
`;

const UserHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: ${props => props.theme.spacing.md};
  padding-bottom: ${props => props.theme.spacing.sm};
  border-bottom: 2px solid ${props => props.theme.colors.border};
`;

const UserTitle = styled.h3`
  font-size: ${props => props.theme.fontSizes.lg};
  font-weight: ${props => props.theme.fontWeights.semibold};
  color: ${props => props.theme.colors.text};
  margin: 0;
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
`;

const CurrentUser = styled.div`
  padding: ${props => props.theme.spacing.sm};
  background: ${props => props.theme.colors.primaryGradient};
  border-radius: ${props => props.theme.borderRadius.md};
  color: white;
  margin-bottom: ${props => props.theme.spacing.md};
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const UserInfo = styled.div`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
`;

const UsersList = styled.div`
  max-height: 200px;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  gap: ${props => props.theme.spacing.xs};
`;

const UserItem = styled.div`
  padding: ${props => props.theme.spacing.sm};
  background: ${props => props.isActive ? props.theme.colors.primaryLight : props.theme.colors.surface};
  border: 1px solid ${props => props.isActive ? props.theme.colors.primary : props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.md};
  transition: all ${props => props.theme.transitions.fast};
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: space-between;
  
  &:hover:not(.active) {
    border-color: ${props => props.theme.colors.primary};
    background: ${props => props.theme.colors.surfaceAlt};
  }
`;

const UserName = styled.div`
  font-weight: ${props => props.theme.fontWeights.medium};
  color: ${props => props.theme.colors.text};
  font-size: ${props => props.theme.fontSizes.sm};
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
`;

const NewUserForm = styled.div`
  padding: ${props => props.theme.spacing.sm};
  background: ${props => props.theme.colors.surface};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.md};
  margin-top: ${props => props.theme.spacing.sm};
`;

const Input = styled.input`
  width: 100%;
  padding: ${props => props.theme.spacing.sm};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.sm};
  font-size: ${props => props.theme.fontSizes.sm};
  margin-bottom: ${props => props.theme.spacing.sm};
  
  &:focus {
    outline: none;
    border-color: ${props => props.theme.colors.primary};
    box-shadow: 0 0 0 2px ${props => props.theme.colors.primary}20;
  }
`;

const FormButtons = styled.div`
  display: flex;
  gap: ${props => props.theme.spacing.sm};
`;

const Button = styled.button`
  flex: 1;
  padding: ${props => props.theme.spacing.sm};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.sm};
  background: ${props => props.primary ? props.theme.colors.primaryGradient : props.theme.colors.surface};
  color: ${props => props.primary ? 'white' : props.theme.colors.text};
  font-size: ${props => props.theme.fontSizes.sm};
  cursor: pointer;
  transition: all ${props => props.theme.transitions.fast};
  display: flex;
  align-items: center;
  justify-content: center;
  gap: ${props => props.theme.spacing.xs};
  
  &:hover {
    background: ${props => props.primary ? props.theme.colors.primary : props.theme.colors.surfaceAlt};
  }
  
  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const IconButton = styled.button`
  padding: ${props => props.theme.spacing.xs};
  border: none;
  background: transparent;
  color: ${props => props.theme.colors.textSecondary};
  cursor: pointer;
  border-radius: ${props => props.theme.borderRadius.sm};
  transition: all ${props => props.theme.transitions.fast};
  
  &:hover {
    background: ${props => props.theme.colors.surfaceAlt};
    color: ${props => props.theme.colors.text};
  }
`;

const AddUserButton = styled(Button)`
  width: 100%;
  justify-content: center;
  background: ${props => props.theme.colors.secondaryGradient};
  color: white;
  margin-top: ${props => props.theme.spacing.sm};
  
  &:hover {
    background: ${props => props.theme.colors.secondary};
  }
`;

function UserSelector({ onUserChange }) {
  const [users, setUsers] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [showNewUserForm, setShowNewUserForm] = useState(false);
  const [newUserName, setNewUserName] = useState('');
  const [editingUser, setEditingUser] = useState(null);
  const [editName, setEditName] = useState('');

  useEffect(() => {
    loadUsers();
  }, []);

  const loadUsers = () => {
    const allUsers = userService.getAllUsers();
    const current = userService.getCurrentUser();
    setUsers(allUsers);
    setCurrentUser(current);
  };

  const handleUserSelect = (user) => {
    userService.switchUser(user.id);
    setCurrentUser(user);
    if (onUserChange) onUserChange(user);
  };

  const handleCreateUser = () => {
    if (newUserName.trim()) {
      const newUser = userService.createUser(newUserName.trim());
      setNewUserName('');
      setShowNewUserForm(false);
      loadUsers();
      handleUserSelect(newUser);
    }
  };

  const handleEditUser = (user) => {
    setEditingUser(user.id);
    setEditName(user.name);
  };

  const handleSaveEdit = () => {
    if (editName.trim() && editingUser) {
      userService.updateUser(editingUser, { name: editName.trim() });
      setEditingUser(null);
      setEditName('');
      loadUsers();
    }
  };

  const handleCancelEdit = () => {
    setEditingUser(null);
    setEditName('');
  };

  return (
    <UserContainer>
      <UserHeader>
        <UserTitle>
          <FiUsers />
          Users
        </UserTitle>
      </UserHeader>

      {currentUser && (
        <CurrentUser>
          <UserInfo>
            <FiUser />
            <span>{currentUser.name}</span>
          </UserInfo>
          <IconButton onClick={() => handleEditUser(currentUser)}>
            <FiEdit3 size={14} />
          </IconButton>
        </CurrentUser>
      )}

      {users.length > 1 && (
        <UsersList>
          {users.map((user) => (
            <UserItem 
              key={user.id}
              isActive={currentUser?.id === user.id}
              onClick={() => user.id !== currentUser?.id && handleUserSelect(user)}
            >
              <UserName>
                <FiUser size={14} />
                {editingUser === user.id ? (
                  <Input
                    value={editName}
                    onChange={(e) => setEditName(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSaveEdit()}
                    onClick={(e) => e.stopPropagation()}
                    autoFocus
                  />
                ) : (
                  user.name
                )}
              </UserName>
              {editingUser === user.id ? (
                <div style={{ display: 'flex', gap: '4px' }}>
                  <IconButton onClick={(e) => { e.stopPropagation(); handleSaveEdit(); }}>
                    <FiCheck size={14} />
                  </IconButton>
                  <IconButton onClick={(e) => { e.stopPropagation(); handleCancelEdit(); }}>
                    <FiX size={14} />
                  </IconButton>
                </div>
              ) : (
                currentUser?.id !== user.id && (
                  <IconButton onClick={(e) => { e.stopPropagation(); handleEditUser(user); }}>
                    <FiEdit3 size={14} />
                  </IconButton>
                )
              )}
            </UserItem>
          ))}
        </UsersList>
      )}

      {showNewUserForm ? (
        <NewUserForm>
          <Input
            placeholder="Enter user name..."
            value={newUserName}
            onChange={(e) => setNewUserName(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleCreateUser()}
            autoFocus
          />
          <FormButtons>
            <Button 
              primary 
              onClick={handleCreateUser}
              disabled={!newUserName.trim()}
            >
              <FiCheck />
              Create
            </Button>
            <Button onClick={() => {
              setShowNewUserForm(false);
              setNewUserName('');
            }}>
              <FiX />
              Cancel
            </Button>
          </FormButtons>
        </NewUserForm>
      ) : (
        <AddUserButton onClick={() => setShowNewUserForm(true)}>
          <FiUserPlus />
          Add New User
        </AddUserButton>
      )}
    </UserContainer>
  );
}

export default UserSelector;
